angular.module("pascalprecht.translate").directive("translateNamespace",translateNamespaceDirective);function translateNamespaceDirective(){return{restrict:"A",scope:true,compile:function(){return{pre:function(b,c,a){b.translateNamespace=getTranslateNamespace(b);if(b.translateNamespace&&a.translateNamespace.charAt(0)==="."){b.translateNamespace+=a.translateNamespace}else{b.translateNamespace=a.translateNamespace}}}}}}function getTranslateNamespace(a){if(a.translateNamespace){return a.translateNamespace}if(a.$parent){return getTranslateNamespace(a.$parent)}}translateNamespaceDirective.displayName="translateNamespaceDirective";