angular.module("globalErrorHandlerModule",["ngStorage"]).factory("myHttpInterceptor",["$rootScope","$q","$injector",function(a,b,c){return{responseError:function(d){c.get("$modal").open({template:'<div class="error_panel">Exception occured</div>Status: '+d.status+"<br/> Reason: "+d.statusText,});return b.reject(d)}}}]).config(function(a){a.interceptors.push("myHttpInterceptor");a.interceptors.push(["$q","$location","$localStorage","jwtHelper",function(b,d,c){return{request:function(e){e.headers=e.headers||{};if(c.jwt){e.headers.Authorization=c.jwt}return e},responseError:function(e){if(e.status===401||e.status===403){d.path("/login")}return b.reject(e)}}}])});